    resizeCanvas() {
        let bounds = this.canvas.getBoundingClientRect()
        
        this.canvas.width = bounds.width
        this.canvas.height = bounds.height
    }

    getBounds() {
        let bounds = {
            left: Infinity,
            right: -Infinity,
            top: Infinity,
            bottom: -Infinity,
            width: 0,
            height: 0,
        }

        for(let node of this.passives) {
            if(node.x < bounds.left) bounds.left = node.x
            if(node.x > bounds.right) bounds.right = node.x
            if(node.x < bounds.top) bounds.top = node.x
            if(node.x > bounds.bottom) bounds.bottom = node.x
        }

        bounds.height = bounds.bottom - bounds.top
        bounds.width = bounds.right - bounds.left

        return bounds
    }

    draw() {
        this.context.fillStyle = 'black'
        this.context.fillRect(0, 0, this.canvas.width, this.canvas.height)

        for(let node of this.passives) {
            this.drawLinks(node)
        }

        for(let node of this.passives) {
            this.drawNode(node)
        }
    }

    getNodeScreenPosition(node) {
        let bounds = this.getBounds()

        let zeroBasedPosition = {
            x: node.x - bounds.top,
            y: node.y - bounds.left,
        }

        let centeredPosition = {
            x: zeroBasedPosition.x - (bounds.width / 2),
            y: zeroBasedPosition.y - (bounds.height / 2),
        }

        let scale = Math.min(
            this.canvas.height / bounds.height,
            this.canvas.width / bounds.width
        )

        let scaledPosition = {
            x: centeredPosition.x * scale,
            y: centeredPosition.y * scale
        }
        
        let position = {
            x: scaledPosition.x + (this.canvas.width / 2),
            y: scaledPosition.y + (this.canvas.height / 2),
        }

        return position
    }

    drawNode(node) { 
        let position = this.getNodeScreenPosition(node)
        
        let size = node.size * 7

        let colors = node.types.map(type => {
            let typeInformation = this.types[type]

            return typeInformation.color
        })

        if(!colors.length) colors.push(this.colors.white)

        for(let color of colors) {
            this.context.fillStyle = `#${color}`
            this.context.fillRect(
                position.x - (size / 2),
                position.y - (size / 2),
                size,
                size
            )

            size -= 2
        }

        if(node.types.length) {
            this.context.fillStyle = 'black'
            this.context.fillRect(
                position.x - (size / 2),
                position.y - (size / 2),
                size,
                size
            )
        }
    }

    drawLinks(node) {
        for(let link of node.links) {
            if(link < node.id) continue // don't redraw

            let other = this.passives.find(n => n.id == link)

            let thisPosition = this.getNodeScreenPosition(node)
            let otherPosition = this.getNodeScreenPosition(other)

            this.context.strokeStyle = '#666'
            this.context.lineWidth = 1

            this.context.moveTo(thisPosition.x, thisPosition.y)
            this.context.lineTo(otherPosition.x, otherPosition.y)
            this.context.stroke()
        }
    }
